import React, { useEffect, useState } from "react";
import {
  PaymentElement,
  useStripe,
  useElements
} from "@stripe/react-stripe-js";

export default function CheckoutForm() {
  const stripe = useStripe();
  const elements = useElements();

  const [message, setMessage] = useState(null);
  const [isLoading, setIsLoading] = useState(false);


  useEffect(() => {

    if (!stripe) {
      return;
    }


    const clientSecret = localStorage.getItem('payment_intent');
    if (!clientSecret) {
      return;
    }

    const queryString = window.location.search;
    const queryParams = {};

    // Split the query string by '&' to get individual key-value pairs
    const pairs = queryString.split('?')[1];
    const newpairs = pairs.split('&');

    newpairs.forEach(pair => {
        // Split each pair by '=' to separate key and value
        const [key, value] = pair.split('=');

        // Add the key-value pair to the queryParams object
        queryParams[key] = value;
    });

    console.log('query parma ',queryParams)

   if(null != queryParams  && null != queryParams.payment_intent_client_secret &&  typeof queryParams.payment_intent_client_secret !== 'undefined'){
        stripe.retrievePaymentIntent(payment_intent_client_secret).then(({ paymentIntent }) => {
          switch (paymentIntent.status) {
              case "succeeded":
                  setMessage("Payment succeeded!");
                  break;
              case "processing":
                  setMessage("Your payment is processing.");
                  break;
              case "requires_payment_method":
                  setMessage("Please enter your payment details");
                  break;
              default:
                  setMessage("Something went wrong.");
                  break;
          }
      });
   }



  }, [stripe]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!stripe || !elements) {
      // Stripe.js hasn't yet loaded.
      // Make sure to disable form submission until Stripe.js has loaded.
      return;
    }

    setIsLoading(true);

    const {error } = await stripe.confirmPayment({
      elements,
      confirmParams: {
        // Make sure to change this to your payment completion page
        return_url: "http://localhost:3000/checkout",
      },
    });

    // This point will only be reached if there is an immediate error when
    // confirming the payment. Otherwise, your customer will be redirected to
    // your `return_url`. For some payment methods like iDEAL, your customer will
    // be redirected to an intermediate site first to authorize the payment, then
    // redirected to the `return_url`.
    if (error.type === "card_error" || error.type === "validation_error") {
      setMessage(error.message);
    } else {
      setMessage("An unexpected error occurred.");
    }

    setIsLoading(false);
  };

  const paymentElementOptions = {
    layout: "tabs"
  }




  return (
    <form id="payment-form" onSubmit={handleSubmit}>

      <PaymentElement id="payment-element" options={paymentElementOptions} />
      <button disabled={isLoading || !stripe || !elements} id="submit" className="submit-button px-4 py-3 rounded-full bg-pink-400 text-white focus:ring focus:outline-none w-full text-xl font-semibold transition-colors">
        <span id="button-text" >
          {isLoading ? <div className="spinner" id="spinner"></div> : "Pay now"}
        </span>
      </button>

      {/* Show any error or success messages */}
      {message && <div id="payment-message">{message}</div>}
    </form>
  );
}
