import './App.css';
import logo                           from './logo.svg';
import {BrowserRouter, Routes, Route} from 'react-router-dom';
import React,{useState,useEffect} from 'react'
import { ToastContainer, toast }      from "react-toastify";
import "react-toastify/dist/ReactToastify.css";

import Navbar            from  './components/Layout/Navbar';
import Footer            from  './components/Layout/Footer';
import Signup            from  './components/Signup';
import Login             from  './components/Login';

import Shop              from  './components/User/Shop';
import Thankyou          from  './components/User/Thankyou';


import AddProduct        from  './components/Admin/Product/AddProduct';
import AllProduct        from  './components/Admin/Product/AllProducts';
import UpdateProduct     from  './components/Admin/Product/UpdateProduct';
import PrivateComponents from  './components/PrivateComponents';

function App() {


  const auth       = localStorage.getItem('user')
  const [nodeapiurl,setNodeapiurl]  = useState("");
  const BASE_URL = process.env.REACT_APP_BASE_URL

  useEffect(()=>{

      setNodeapiurl(process.env.REACT_APP_NODE_API_URL);
    })


  return (

    <div className="App">
     <BrowserRouter>
       <Navbar/>

        <Routes>

          <Route element={<PrivateComponents />} >
              <Route path={BASE_URL} element={<AllProduct />} />
              <Route path={BASE_URL+'/add'} element={<AddProduct />} />
              <Route path={BASE_URL+'/update/:id'} element={<UpdateProduct />} />
              <Route path={BASE_URL+'/logout'} element={<h1>Logout User</h1>} />

          </Route>

          <Route path={BASE_URL+'/signup'} element={<Signup />} />
          <Route path={BASE_URL+'/login'} element={<Login />} />
          <Route path={BASE_URL+'/shop'} element={<Shop />} />
          <Route path={BASE_URL+'/thankyou'} element={<Thankyou />} />

        </Routes>
        <Footer />
      </BrowserRouter>
        <ToastContainer />
    </div>
  );
}

export default App;
